// -------------------------------// adapted from Kevin T. Manley// CSE 593// -------------------------------package hotelcode.ResImpl;import carcode.ResImpl.Car;import servercode.ResImpl.*;public class Hotel extends ReservableItem{		public Hotel( String location, int count, int price )		{			super( location, count, price );		}		public String getKey()		{			return Hotel.getKey( getLocation() );		}		public static String getKey( String location )		{			String s = "room-" + location  ;			return s.toLowerCase();		}				public Hotel getCopy(){	    	Hotel copy = new Hotel(this.getLocation(), this.getCount(), this.getPrice());	    	copy.setReserved(this.getReserved());	    		    	return copy;	    }}